import React, { useState, useEffect } from 'react';

const TransactionForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    type: 'expense',
    category: '',
    amount: '',
    date: new Date().toISOString().split('T')[0],
    goalId: '' 
  });

  const [goals, setGoals] = useState([]); 

  //  CATEGORIES ARRAYS
  const expenseCategories = [
    'food', 'transportation', 'utilities', 'entertainment', 
    'healthcare', 'shopping', 'education', 'travel', 'housing',
    'insurance', 'debt', 'personal_care', 'gifts', 'charity', 'other'
  ];

  const incomeCategories = [
    'salary', 'freelance', 'business', 'investment', 'bonus', 
    'rental', 'dividend', 'interest', 'refund', 'other'
  ];

  //  DYNAMICALLY GET CATEGORIES BASED ON TRANSACTION TYPE
  const categories = formData.type === 'income' ? incomeCategories : expenseCategories;

  // Get token from localStorage
  const getToken = () => {
    return localStorage.getItem('token');
  };

  // Fetch goals for the dropdown
  useEffect(() => {
    const token = getToken();
    fetch('http://localhost:5000/api/goal', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then((res) => res.json())
      .then((data) => setGoals(data))
      .catch((err) => console.error('Error fetching goals:', err));
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.category || !formData.amount || !formData.date) {
      alert('Please fill in all fields');
      return;
    }

    const submissionData = {
      ...formData,
      amount: Number(formData.amount),
      description: formData.category,
      goalId: formData.goalId || undefined
    };

    console.log('Submitting transaction:', submissionData);
    onSubmit(submissionData);
    
    // Reset form
    setFormData({
      type: 'expense',
      category: '',
      amount: '',
      date: new Date().toISOString().split('T')[0],
      goalId: '' 
    });
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white p-4 rounded-lg shadow space-y-3"
    >
      <div className="flex gap-3">
        <select
          name="type"
          value={formData.type}
          onChange={handleChange}
          className="w-1/3 border rounded-lg px-3 py-2 text-sm"
          required
        >
          <option value="income">Income</option>
          <option value="expense">Expense</option>
        </select>
        
        <select
          name="category"
          value={formData.category}
          onChange={handleChange}
          className="flex-1 border rounded-lg px-3 py-2 text-sm"
          required
        >
          <option value="">Select Category</option>
          {categories.map(category => (
            <option key={category} value={category}>
              {category.charAt(0).toUpperCase() + category.slice(1).replace('_', ' ')}
            </option>
          ))}
        </select>
      </div>
      
      <div className="flex gap-3">
        <input
          type="number"
          name="amount"
          value={formData.amount}
          onChange={handleChange}
          placeholder="Amount (required)"
          className="flex-1 border rounded-lg px-3 py-2 text-sm"
          required
          min="0.01"
          step="0.01"
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          className="w-1/2 border rounded-lg px-3 py-2 text-sm"
          required
        />
      </div>

      {/* Goal Selection Dropdown */}
      <div>
        <select
          name="goalId"
          value={formData.goalId}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2 text-sm"
        >
          <option value="">-- Allocate to Goal (Optional) --</option>
          {goals.map(goal => (
            <option key={goal._id} value={goal._id}>
              {goal.name} - Target: KSH {goal.targetAmount?.toLocaleString()}
            </option>
          ))}
        </select>
        <p className="text-xs text-gray-500 mt-1">
          Select a goal to automatically track progress from this transaction
        </p>
      </div>
      
      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
      >
        Add Transaction
      </button>
    </form>
  );
};

export default TransactionForm;
