import React, { useState, useEffect } from 'react';

const GoalsCard = ({ goal, onUpdate, onDelete }) => {
  const [progress, setProgress] = useState(0);
  const [linkedTransactions, setLinkedTransactions] = useState([]);
  const [totalSaved, setTotalSaved] = useState(0);
  const [loading, setLoading] = useState(true);

  const getToken = () => {
    return localStorage.getItem('token');
  };

  useEffect(() => {
    const fetchGoalTransactions = async () => {
      setLoading(true);
      try {
        const token = getToken();
        
        console.log(`🎯 [DEBUG] Checking goal: ${goal.name} (ID: ${goal._id})`);
        console.log(`🎯 [DEBUG] Goal current amount in DB: ${goal.currentAmount}`);
        
        // Fetch transactions for this goal
        const response = await fetch(`http://localhost:5000/api/transaction?goalId=${goal._id}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        console.log(`🎯 [DEBUG] API response status: ${response.status}`);

        if (response.ok) {
          const result = await response.json();
          console.log(`🎯 [DEBUG] Full API response:`, result);
          
          const transactions = result.data?.transactions || [];
          console.log(`🎯 [DEBUG] Found ${transactions.length} transactions for goal "${goal.name}"`);
          
          setLinkedTransactions(transactions);
          
          // Show all transactions and their types
          transactions.forEach(tx => {
            console.log(`   📝 Transaction: ${tx.description} | Type: ${tx.type} | Amount: ${tx.amount} | GoalId: ${tx.goalId}`);
          });
          
          // Calculate total from INCOME transactions only
          const incomeTransactions = transactions.filter(tx => tx.type === 'income');
          const incomeTotal = incomeTransactions.reduce((sum, tx) => sum + tx.amount, 0);
          
          console.log(`💰 [DEBUG] Income transactions: ${incomeTransactions.length}, Total: ${incomeTotal}`);
          
          setTotalSaved(incomeTotal);
          
          // Calculate progress
          const progressPercentage = goal.targetAmount > 0 
            ? (incomeTotal / goal.targetAmount) * 100 
            : 0;
          
          console.log(`📊 [DEBUG] Progress calculation: ${incomeTotal} / ${goal.targetAmount} = ${progressPercentage}%`);
          setProgress(Math.min(progressPercentage, 100));
          
          // Update goal in database if needed
          if (incomeTotal !== goal.currentAmount) {
            console.log(`🔄 [DEBUG] NEEDS UPDATE: DB has ${goal.currentAmount}, calculated ${incomeTotal}`);
            console.log(`🔄 [DEBUG] Calling onUpdate for goal ${goal._id}`);
            
            await onUpdate(goal._id, { 
              currentAmount: incomeTotal 
            });
            
            console.log(`✅ [DEBUG] Goal update completed`);
          } else {
            console.log(`✅ [DEBUG] Goal amount is up to date: ${goal.currentAmount}`);
          }
        } else {
          console.error(`❌ [DEBUG] API error: ${response.status}`);
        }
      } catch (err) {
        console.error(`❌ [DEBUG] Network error:`, err);
      } finally {
        setLoading(false);
      }
    };

    fetchGoalTransactions();
  }, [goal._id, goal.targetAmount, goal.currentAmount, goal.name, onUpdate]);

  const handleDelete = () => {
    if (window.confirm(`Delete "${goal.name}"?`)) {
      onDelete(goal._id);
    }
  };

  if (loading) {
    return (
      <div className="bg-white rounded-lg shadow-md p-4 border border-gray-200">
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
          <div className="h-3 bg-gray-200 rounded w-1/2 mb-4"></div>
          <div className="h-2 bg-gray-200 rounded w-full mb-1"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-4 border border-gray-200">
      <div className="flex justify-between items-start mb-3">
        <h3 className="text-lg font-semibold text-gray-800">{goal.name}</h3>
        <button
          onClick={handleDelete}
          className="text-red-500 hover:text-red-700 text-sm font-medium"
        >
          Delete
        </button>
      </div>
      
      <div className="mb-3">
        <p className="text-gray-600">
          Target: <span className="font-medium">KSH {goal.targetAmount?.toLocaleString()}</span>
        </p>
        <p className="text-gray-600">
          Saved: <span className="font-medium">KSH {totalSaved.toLocaleString()}</span>
          {goal.currentAmount !== totalSaved && (
            <span className="text-xs text-orange-500 ml-2">
              (DB: {goal.currentAmount || 0})
            </span>
          )}
        </p>
        <p className="text-sm text-gray-500">
          {linkedTransactions.filter(tx => tx.type === 'income').length} income transactions
        </p>
      </div>

      {/* Progress Bar - VISIBLE TEST */}
      <div className="mb-4">
        <div className="flex justify-between text-sm text-gray-600 mb-1">
          <span>Progress</span>
          <span>{progress.toFixed(1)}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-3">
          <div
            className="bg-green-500 h-3 rounded-full transition-all duration-500"
            style={{ width: `${progress}%` }}
          ></div>
        </div>
        <div className="text-xs text-gray-400 text-center mt-1">
          {progress.toFixed(1)}% Complete
        </div>
      </div>

      {/* Debug Info */}
      <div className="mt-3 p-2 bg-gray-100 rounded text-xs">
        <div><strong>Debug Info:</strong></div>
        <div>Goal ID: {goal._id}</div>
        <div>Total Transactions: {linkedTransactions.length}</div>
        <div>Income Transactions: {linkedTransactions.filter(tx => tx.type === 'income').length}</div>
        <div>Progress: {progress}%</div>
      </div>
    </div>
  );
};

export default GoalsCard;
