// src/Components/Goals/GoalsCard.js
import React, { useState, useEffect } from 'react';

const GoalsCard = ({ goal, onUpdate, onDelete }) => {
  const [progress, setProgress] = useState(0);
  const [linkedTransactions, setLinkedTransactions] = useState([]);

  // Get token from localStorage
  const getToken = () => {
    return localStorage.getItem('token');
  };

  // Fetch transactions linked to this goal and calculate progress
  useEffect(() => {
    const token = getToken();
    
    // Fetch all transactions and filter those linked to this goal
    fetch('http://localhost:5000/api/transaction', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then((res) => res.json())
      .then((result) => {
        const transactions = result.data?.transactions || [];
        
        // Filter transactions linked to this goal
        const goalTransactions = transactions.filter(tx => tx.goalId === goal._id);
        setLinkedTransactions(goalTransactions);
        
        // Calculate total saved from transactions
        const totalSaved = goalTransactions.reduce((sum, tx) => {
          // Only count income transactions toward goal savings
          return tx.type === 'income' ? sum + tx.amount : sum;
        }, 0);
        
        // Calculate progress percentage
        const progressPercentage = goal.targetAmount > 0 
          ? (totalSaved / goal.targetAmount) * 100 
          : 0;
        
        setProgress(Math.min(progressPercentage, 100)); // Cap at 100%
        
        // If we have new savings data, update the goal
        if (totalSaved !== goal.currentAmount) {
          onUpdate(goal._id, { ...goal, currentAmount: totalSaved });
        }
      })
      .catch((err) => console.error('Error fetching transactions for goal:', err));
  }, [goal._id, goal.targetAmount]);

  const handleDelete = () => {
    if (window.confirm(`Are you sure you want to delete "${goal.name}"?`)) {
      onDelete(goal._id);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 border border-gray-200">
      <div className="flex justify-between items-start mb-3">
        <h3 className="text-lg font-semibold text-gray-800">{goal.name}</h3>
        <button
          onClick={handleDelete}
          className="text-red-500 hover:text-red-700 text-sm font-medium"
        >
          Delete
        </button>
      </div>
      
      <div className="mb-3">
        <p className="text-gray-600">
          Target: <span className="font-medium">KSH {goal.targetAmount?.toLocaleString()}</span>
        </p>
        <p className="text-gray-600">
          Saved: <span className="font-medium">KSH {goal.currentAmount?.toLocaleString() || 0}</span>
        </p>
      </div>

      {/* Progress Bar */}
      <div className="mb-4">
        <div className="flex justify-between text-sm text-gray-600 mb-1">
          <span>Progress</span>
          <span>{progress.toFixed(1)}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-3">
          <div
            className="bg-green-500 h-3 rounded-full transition-all duration-500"
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      </div>

      {/* Linked Transactions Summary */}
      {linkedTransactions.length > 0 && (
        <div className="mt-3 pt-3 border-t border-gray-200">
          <p className="text-sm text-gray-600 mb-2">
            Linked Transactions: {linkedTransactions.length}
          </p>
          <div className="text-xs text-gray-500 space-y-1 max-h-20 overflow-y-auto">
            {linkedTransactions.slice(0, 5).map(tx => (
              <div key={tx._id} className="flex justify-between">
                <span>{tx.description}</span>
                <span className={tx.type === 'income' ? 'text-green-600' : 'text-red-600'}>
                  {tx.type === 'income' ? '+' : '-'}KSH {tx.amount?.toLocaleString()}
                </span>
              </div>
            ))}
            {linkedTransactions.length > 5 && (
              <p className="text-gray-400">...and {linkedTransactions.length - 5} more</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default GoalsCard;
