import React, { useState, useEffect } from 'react';
import TransactionForm from './TransactionForm';
import TransactionItem from './TransactionItem';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);

  // Get token from localStorage
  const getToken = () => {
    return localStorage.getItem('token');
  };

  // Fetch transactions from backend
  // Fetch transactions from backend
const fetchTransactions = async () => {
  try {
    const token = getToken();
    const response = await fetch('http://localhost:5000/api/transaction', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    console.log('ðŸ” DEBUG - Response status:', response.status);
    
    if (response.ok) {
      const data = await response.json();
      console.log('ðŸ” DEBUG - Received data:', data);
      console.log('ðŸ” DEBUG - Data type:', typeof data);
      console.log('ðŸ” DEBUG - Is array?', Array.isArray(data));
      
      // âœ… Handle different response formats
      if (Array.isArray(data)) {
        setTransactions(data);
      } else if (data.transactions && Array.isArray(data.transactions)) {
        setTransactions(data.transactions); // If backend returns { transactions: [] }
      } else if (data.data && Array.isArray(data.data)) {
        setTransactions(data.data); // If backend returns { data: [] }
      } else {
        console.error('Unexpected data format:', data);
        setTransactions([]); // Set empty array as fallback
      }
    } else {
      console.error('Failed to fetch transactions, status:', response.status);
      setTransactions([]); // Set empty array on error
    }
  } catch (error) {
    console.error('Error fetching transactions:', error);
    setTransactions([]); // Set empty array on error
  }
};
  // Add transaction to backend
  const addTransaction = async (transactionData) => {
    try {
      const token = getToken();
      
      const backendData = {
        type: transactionData.type,
        category: transactionData.category,
        amount: Number(transactionData.amount),
        date: transactionData.date,
        description: transactionData.category
      };

      console.log('Sending transaction:', backendData);

      const response = await fetch('http://localhost:5000/api/transaction', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(backendData),
      });

      if (response.ok) {
        const newTransaction = await response.json();
        setTransactions(prev => [...prev, newTransaction]);
        console.log('Transaction saved successfully:', newTransaction);
      } else {
        const errorData = await response.json();
        console.error('Failed to add transaction:', errorData);
      }
    } catch (error) {
      console.error('Error adding transaction:', error);
    }
  };

  // Delete transaction from backend
  const deleteTransaction = async (id) => {
    try {
      const token = getToken();
      
      const response = await fetch(`http://localhost:5000/api/transaction/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setTransactions(prev => prev.filter(tx => tx._id !== id));
      } else {
        console.error('Failed to delete transaction');
      }
    } catch (error) {
      console.error('Error deleting transaction:', error);
    }
  };

  // Fetch transactions on component mount
  useEffect(() => {
    fetchTransactions();
  }, []);

  return (
    <div className="max-w-2xl mx-auto p-6 space-y-6">
      <h1 className="text-2xl font-bold text-gray-800">Transactions</h1>
      
      <TransactionForm onSubmit={addTransaction} />

      <div className="space-y-3">
        {transactions.length === 0 ? (
          <p className="text-gray-500 text-center py-8">No transactions yet</p>
        ) : (
          transactions.map((tx) => (
            <TransactionItem
              key={tx._id}
              transaction={tx}
              onDelete={deleteTransaction}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default TransactionList;
