import React, { useState } from 'react';
import GoalsForm from './GoalsForm';
import GoalsCard from './GoalsCard';

const GoalsManager = () => {
  const [goals, setGoals] = useState([]);

  // Fetch goals from backend on mount
  useEffect(() => {
    fetch('http://localhost:5000/api/goals')
      .then((res) => res.json())
      .then((data) => setGoals(data))
      .catch((err) => console.error('Error fetching goals:', err));}, []);

  const addGoal = (goal) => {
    setGoals([...goals, { ...goal, id: Date.now() }]);

    // Save to backend first
    fetch('http://localhost:5000/api/goals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(goal),
    })
      .then((res) => res.json())
      .then((newGoal) => setGoals([...goals, newGoal]))
      .catch((err) => console.error('Error adding goal:', err));

  };

  const updateGoal = (id, updatedGoal) => {
    setGoals(goals.map((goal) => (goal.id === id ? updatedGoal : goal)));

    fetch(`http://localhost:5000/api/goals/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedGoal),
    })
      .then((res) => res.json())
      .then((savedGoal) => {
        setGoals(goals.map((goal) => (goal.id === id ? savedGoal : goal)));
      })
      .catch((err) => console.error('Error updating goal:', err));
  };

  const deleteGoal = (id) => {
    setGoals(goals.filter((goal) => goal.id !== id));
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">My Goals</h1>

      {/* Add Goal Form */}
      <GoalsForm onAddGoal={addGoal} />

      {/* Goal List */}
      <div className="mt-6 grid gap-4">
        {goals.length > 0 ? (
          goals.map((goal) => (
            <GoalsCard
              key={goal.id}
              goal={goal}
              onUpdate={updateGoal}
              onDelete={deleteGoal}
            />
          ))
        ) : (
          <p className="text-gray-600">No goals yet. Start by adding one above.</p>
        )}
      </div>
    </div>
  );
};

export default GoalsManager;
